sourcesContent:
import jwt from "jsonwebtoken";
import { Op, Sequelize } from "sequelize";
import db from "../models/index";


const gethome = (req, res) => {
    return res.json("toan dep rai")
}

const login = async(req, res) => {

    try {
        
        
        let username = req.body.username;
        let password = req.body.password;
        let data = await db.User.findOne({
            where: {
                username: username,
                password:password
            },
            raw:true
        })
        const token = jwt.sign({id:data.id,name:data.username}, "toan");
        return res.status(200).json(token);
      

    } catch (e) {
        console.log(e)
    }
    
}

const checklogin= async(req, res) => {

    try {
        const token = req.body.token;
        
        const decoded = jwt.verify(token, "toan");
       
         let data = await db.User.findOne({
             where: {
                id:decoded.id,
                username: decoded.name,
                
            },
            raw:true
         })
        if (!data) {
            return res.status(200).json("khong phai nguoi dung")
        } else {
            
            return res.status(200).json({
                mesasge: true,
                data:data
           })
        }
      
      
      

    } catch (e) {
        console.log(e)
    }
    
}

const pagination = async(req, res) => {

    try {
        let search = req.query.search;
        
        if (search) {
            let limit = 5;
            let offset = (+req.query.page -1 )*limit
    
            let count = await db.User.count();
            let respon = await db.User.findAll({
                
                where: {
                    username: {
                        [Op.like]:`%${search}%`
                    }
                },
                limit, offset,
                
            });
            res.status(200).json({
                data: respon,
                limit: limit,
                total: count,
                page:1
            })
      
        } else {
            
            let limit = 5;
            let offset = (+req.query.page -1 )*limit
    
            let count = await db.User.count();
            let respon = await db.User.findAll({ limit, offset });
            res.status(200).json({
                data: respon,
                limit: limit,
                total: count,
                page:1
            })
          
        }

      

    } catch (e) {
        console.log(e)
    }
    
}
module.exports = {
    gethome,
    login,
    checklogin,
    pagination
}

