import jwt from "jsonwebtoken";
import { Op, Sequelize } from "sequelize";
import db from "../models/index";
const bcrypt = require("bcrypt");

const login = async (req, res) => {
  try {
    let adminname = req.body.adminname;
    let password = req.body.password;
    

    let data = await db.Admin.findOne({
      where: {
        adminuser: adminname,
        password: password,
      },
      raw: true,
    });

    const token = jwt.sign({ id: data.id, name: data.adminname }, "toan");
    res.cookie("myCookie", token, { maxAge: 900000, httpOnly: true });
    return res.status(200).json(token);
  } catch (e) {
    console.log(e);
  }
};

const checklogin = async (req, res) => {
  try {
    const token = req.body.token;

    const decoded = jwt.verify(token, "toan");

    let data = await db.Admin.findOne({
      where: {
        id: decoded.id,
        adminname: decoded.name,
      },
      raw: true,
    });
    if (!data) {
      return res.status(200).json("khong phai nguoi dung");
    } else {
      return res.status(200).json({
        mesasge: true,
        data: data,
      });
    }
  } catch (e) {
    console.log(e);
  }
};

// category
const category = async (req, res) => {
  try {
    let category = req.body.category;
    if (category === "" || category === null) return;
    await db.Category.create({ categoryname: category });

    return res.status(200).json({
      mesasge: 0,
      data: "thanh cong",
    });
  } catch (e) {
    console.log(e);
  }
};

const categoryall = async (req, res) => {
  try {
    let data = await db.Category.findAll();

    return res.status(200).json({
      mesasge: 0,
      data: data,
    });
  } catch (e) {
    console.log(e);
  }
};

const categoryfix = async (req, res) => {
  try {
    let id = req.body.id;
    let data = await db.Category.findOne({
      where: {
        id: id,
      },
    });

    return res.status(200).json({
      mesasge: 0,
      data: data,
    });
  } catch (e) {
    console.log(e);
  }
};
const categoryupdate = async (req, res) => {
  try {
    let id = req.body.id;
    let category = req.body.category;

    await db.Category.update(
      { categoryname: category },
      {
        where: {
          id: id,
        },
      }
    );

    return res.status(200).json({
      mesasge: 0,
      data: "thanh  cong",
    });
  } catch (e) {
    console.log(e);
  }
};
const categorydestroy = async (req, res) => {
  try {
    let id = req.body.id;

    await db.Category.destroy({
      where: {
        id: id,
      },
    });

    return res.status(200).json({
      mesasge: 0,
      data: "thanh  cong",
    });
  } catch (e) {
    console.log(e);
  }
};

// brand

const brand = async (req, res) => {
  try {
    let category = req.body.brand;
    if (category === "" || category === null) return;
    await db.Brand.create({ brandname: category });

    return res.status(200).json({
      mesasge: 0,
      data: "thanh cong",
    });
  } catch (e) {
    console.log(e);
  }
};

const brandall = async (req, res) => {
  try {
    let data = await db.Brand.findAll();

    return res.status(200).json({
      mesasge: 0,
      data: data,
    });
  } catch (e) {
    console.log(e);
  }
};

const brandfix = async (req, res) => {
  try {
    let id = req.body.id;
    let data = await db.Brand.findOne({
      where: {
        id: id,
      },
    });

    return res.status(200).json({
      mesasge: 0,
      data: data,
    });
  } catch (e) {
    console.log(e);
  }
};
const brandupdate = async (req, res) => {
  try {
    let id = req.body.id;
    let category = req.body.brand;

    await db.Brand.update(
      { brandname: category },
      {
        where: {
          id: id,
        },
      }
    );

    return res.status(200).json({
      mesasge: 0,
      data: "thanh  cong",
    });
  } catch (e) {
    console.log(e);
  }
};
const branddestroy = async (req, res) => {
  try {
    let id = req.body.id;

    await db.Brand.destroy({
      where: {
        id: id,
      },
    });

    return res.status(200).json({
      mesasge: 0,
      data: "thanh  cong",
    });
  } catch (e) {
    console.log(e);
  }
};

// product

const getallcategory = async (req, res) => {
  try {
    let data = await db.Category.findAll();

    return res.status(200).json({
      mesasge: 0,
      data: data,
    });
  } catch (e) {
    console.log(e);
  }
};

const getallbrand = async (req, res) => {
  try {
    let data = await db.Brand.findAll();

    return res.status(200).json({
      mesasge: 0,
      data: data,
    });
  } catch (e) {
    console.log(e);
  }
};

const creatproduct = async (req, res) => {
  try {
    let data = await db.Product.findOrCreate({
      where: {
        productname: req.body.productname,
      },
      defaults: {
        productname: req.body.productname,
        brand_id: req.body.brand_id,
        category_id: req.body.category_id,
        productdes: req.body.productdes,
        price: +req.body.price,
        type: req.body.type,
        image: req.body.image,
      },
      raw: true,
      nest: true,
    });

    if (data && data[1] === false) {
      return res.status(200).json({
        mesasge: 1,
        data: "that bai",
      });
    }

    return res.status(200).json({
      mesasge: 0,
      data: "thanh cong",
    });
  } catch (e) {
    console.log(e);
  }
};
const updateproduct = async (req, res) => {
  try {
    await db.Product.update(
      {
        productname: req.body.productname,
        brand_id: req.body.brand_id,
        category_id: req.body.category_id,
        productdes: req.body.productdes,
        price: +req.body.price,
        type: req.body.type,
        image: req.body.image,
      },
      {
        where: {
          id: req.body.id,
        },

        raw: true,
        nest: true,
      }
    );

    return res.status(200).json({
      mesasge: 0,
      data: "thanh cong",
    });
  } catch (e) {
    console.log(e);
  }
};
const deleteproduct = async (req, res) => {
  try {
    let id = req.body.id;
    await db.Product.destroy({
      where: {
        id: id,
      },
    });

    return res.status(200).json({
      mesasge: 0,
      data: "thanh cong",
    });
  } catch (e) {
    console.log(e);
  }
};

const getallproduct = async (req, res) => {
  try {
    let type = req.body.type;
    let check = req.body.check;
    if (type === "all") {
      if (check) {
        if (check === "reduce") {
          let data = await db.Product.findAll({
            order: [["price", "ASC"]],
            include: [{ model: db.Star, as: "starall" }],
            limit: 8,
            raw: false,
            nest: true,
          });

          if (data) {
            for (let i = 0; i < data.length; i++) {
              data[i].image = new Buffer(data[i].image, "base64").toString(
                "binary"
              );
            }
          }

          return res.status(200).json({
            mesasge: 0,
            data: data,
          });
        } else {
          let data = await db.Product.findAll({
            order: [["price", "DESC"]],
            include: [{ model: db.Star, as: "starall" }],
            limit: 8,
            raw: false,
            nest: true,
          });

          if (data) {
            for (let i = 0; i < data.length; i++) {
              data[i].image = new Buffer(data[i].image, "base64").toString(
                "binary"
              );
            }
          }

          return res.status(200).json({
            mesasge: 0,
            data: data,
          });
        }
      } else {
        let data = await db.Product.findAll({
          include: [{ model: db.Star, as: "starall" }],
          limit: 8,
          raw: false,
          nest: true,
        });

        if (data) {
          for (let i = 0; i < data.length; i++) {
            data[i].image = new Buffer(data[i].image, "base64").toString(
              "binary"
            );
          }
        }

        return res.status(200).json({
          mesasge: 0,
          data: data,
        });
      }
    } else {
      let data = await db.Product.findAll({
        where: {
          id: +type,
        },
        include: [{ model: db.Star, as: "starall" }],
        raw: false,
        nest: true,
      });

      if (data) {
        for (let i = 0; i < data.length; i++) {
          data[i].image = new Buffer(data[i].image, "base64").toString(
            "binary"
          );
        }
      }

      return res.status(200).json({
        mesasge: 0,
        data: data,
      });
    }
  } catch (e) {
    console.log(e);
  }
};

const gettypeproduct = async (req, res) => {
  try {
    let type = req.body.type;

    let data = await db.Product.findAll({
      where: {
        type: +type,
      },
      include: [{ model: db.Star, as: "starall" }],
      limit: 8,
      raw: false,
      nest: true,
    });

    if (data) {
      for (let i = 0; i < data.length; i++) {
        data[i].image = new Buffer(data[i].image, "base64").toString("binary");
      }
    }

    return res.status(200).json({
      mesasge: 0,
      data: data,
    });
  } catch (e) {
    console.log(e);
  }
};
const getalltypeproduct = async (req, res) => {
  try {
    let type = req.body.type;
    let check = req.body.check;
    let limit = 9;
    let offset = (+req.body.page - 1) * limit;

    let brand = await db.Category.findAll({
      where: {
        categoryname: type,
      },

      raw: true,
      nest: true,
    });

    let count = await db.Product.findAll({
      where: {
        category_id: brand[0].id,
      },
    });

    if (brand && check) {
      if (check === "reduce") {
        let data = await db.Product.findAll({
          order: [["price", "ASC"]],
          where: {
            category_id: brand[0].id,
          },
          include: [{ model: db.Star, as: "starall" }],
          limit,
          offset,
          raw: false,
          nest: true,
        });

        if (data) {
          for (let i = 0; i < data.length; i++) {
